    //         {isComponentVisible && (
    //           <Modal>
    //             <AuthForms screen={authScreen} />
    //           </Modal>
    //         )}
    //       </TagProvider>
    //     </FetchProvider>
    //   </AuthProvider>
    // </ModalContext.Provider>
  )
}

export default MyApp
